import React, { useEffect, useState, useCallback } from 'react';
import './App.css'; // For styling

function App() {
  const [pokemon, setPokemon] = useState(null);
  const [loading, setLoading] = useState(false);
  const [query, setQuery] = useState('');
  const [isAutoSearch, setIsAutoSearch] = useState(false);

  const fetchPokemon = useCallback(async () => {
    if (!query) return;

    setLoading(true);
    try {
      const res = await fetch(`http://localhost:3001/api/pokemon/${query}`);
      const data = await res.json();
      setPokemon(data);
    } catch (err) {
      console.error('Fetch error:', err);
      setPokemon(null);
    }
    setLoading(false);
  }, [query]);

  useEffect(() => {
    if (isAutoSearch && query) {
      fetchPokemon();
    }
  }, [fetchPokemon, query, isAutoSearch]);

  return (
    <div className="app">
      <h1 className="title">Pokémon Search</h1>

      <div className="search-container">
        <input
          value={query}
          onChange={(e) => setQuery(e.target.value.toLowerCase())}
          placeholder="Enter Pokémon name"
          className="search-input"
        />

        <label className="switch">
          <input
            type="checkbox"
            checked={isAutoSearch}
            onChange={() => setIsAutoSearch((prev) => !prev)}
          />
          <span className="slider"></span>
        </label>
        <span>{isAutoSearch ? 'Auto Search' : 'Manual Search'}</span>

        {!isAutoSearch && (
          <button className="search-button" onClick={fetchPokemon}>
            Search
          </button>
        )}
      </div>

      {loading && <p className="loading">Loading...</p>}

      {pokemon && pokemon.name ? (
        <div className="pokemon-details">
          <div className="card-container">
            <div className="pokemon-image-card">
              <img src={pokemon.sprites.front_default} alt={pokemon.name} className="pokemon-image" />
            </div>

            <div className="pokemon-text-card">
              <h2 className="pokemon-name">{pokemon.name.toUpperCase()}</h2>

              <div className="pokemon-info">
                <p><strong>Height:</strong> {pokemon.height} decimeters</p>
                <p><strong>Weight:</strong> {pokemon.weight} hectograms</p>

                <p><strong>Types:</strong> {pokemon.types.map((t) => t.type.name).join(', ')}</p>
                <p><strong>Abilities:</strong> {pokemon.abilities.map((a) => a.ability.name).join(', ')}</p>

                <div className="pokemon-stats">
                  <h3>Stats</h3>
                  <ul>
                    {pokemon.stats.map((stat) => (
                      <li key={stat.stat.name}>
                        {stat.stat.name}: {stat.base_stat}
                      </li>
                    ))}
                  </ul>
                </div>

                <div className="pokemon-moves">
                  <h3>Moves</h3>
                  <ul>
                    {pokemon.moves.slice(0, 10).map((move) => (
                      <li key={move.move.name}>{move.move.name}</li>
                    ))}
                  </ul>
                </div>
              </div>

              <div className="pokemon-other-info">
                <h3>Other Information</h3>
                <p><strong>Held Items:</strong> {pokemon.held_items ? pokemon.held_items.map(item => item.item.name).join(', ') : 'No items available'}</p>
                <p><strong>Species:</strong> {pokemon.species.name}</p>
              </div>
            </div>
          </div>
        </div>
      ) : !loading && <p className="no-results">No Pokémon found.</p>}
    </div>
  );
}

export default App;
