import React, { useState, useEffect, useCallback } from 'react';

function App() {
  const [pokemon, setPokemon] = useState(null);
  const [loading, setLoading] = useState(false);
  const [query, setQuery] = useState("");
  const [searchType, setSearchType] = useState("auto"); // 'auto' for auto search, 'manual' for manual search

  // Fungsi untuk mengambil data Pokémon
  const fetchPokemon = useCallback(async () => {
    if (!query) return;  // Menghindari pemanggilan API jika query kosong
    setLoading(true);
    try {
      const res = await fetch(`http://localhost:3001/api/pokemon/${query}`);
      const data = await res.json();
      setPokemon(data);
    } catch (err) {
      console.error("Fetch error:", err);
      setPokemon(null);
    }
    setLoading(false);
  }, [query]);

  // Efek untuk memanggil fetchPokemon saat query berubah, tergantung pada searchType
  useEffect(() => {
    if (searchType === 'auto') {
      fetchPokemon(); // Pencarian otomatis setiap kali query berubah
    }
  }, [fetchPokemon, query, searchType]);

  // Fungsi untuk menangani input pada kolom pencarian
  const handleInputChange = (e) => {
    setQuery(e.target.value.toLowerCase());
  };

  // Fungsi untuk menangani pencarian manual (tombol Enter atau klik Search)
  const handleManualSearch = (e) => {
    if (e.key === 'Enter' || e.type === 'click') {
      fetchPokemon();
    }
  };

  return (
    <div style={{ padding: 20, fontFamily: 'sans-serif' }}>
      <h1>Pokémon Search</h1>

      {/* Switch untuk memilih tipe pencarian */}
      <label>
        Auto Search:
        <input
          type="checkbox"
          checked={searchType === "auto"}
          onChange={() => setSearchType(searchType === "auto" ? "manual" : "auto")}
        />
      </label>

      <input
        value={query}
        onChange={handleInputChange}
        onKeyPress={handleManualSearch}  // Menangani Enter pada input
        placeholder="Enter Pokémon name"
      />
      <button onClick={handleManualSearch}>Search</button>

      {loading && <p>Loading...</p>}

      {pokemon && pokemon.name ? (
        <div style={{ marginTop: 20 }}>
          <h2>{pokemon.name.toUpperCase()}</h2>
          <img src={pokemon.sprites.front_default} alt={pokemon.name} />
          <p>Height: {pokemon.height}</p>
          <p>Weight: {pokemon.weight}</p>
          <p>Type: {pokemon.types.map(t => t.type.name).join(', ')}</p>
        </div>
      ) : !loading && <p>No Pokémon found.</p>}
    </div>
  );
}

export default App;
