import React, { useEffect, useState, useCallback } from 'react';

function App() {
  const [pokemon, setPokemon] = useState(null);
  const [loading, setLoading] = useState(false);
  const [query, setQuery] = useState("pikachu");

  const fetchPokemon = useCallback(async () => {
    setLoading(true);
    try {
      const res = await fetch(`http://localhost:3001/api/pokemon/${query}`);
      const data = await res.json();
      setPokemon(data);
    } catch (err) {
      console.error("Fetch error:", err);
      setPokemon(null);
    }
    setLoading(false);
  }, [query]); // dependency on 'query' to refetch when it changes

  useEffect(() => {
    fetchPokemon();
  }, [fetchPokemon]); // Now fetchPokemon is stable and will not trigger unnecessary re-renders

  return (
    <div style={{ padding: 20, fontFamily: 'sans-serif' }}>
      <h1>Pokémon Search</h1>
      <input
        value={query}
        onChange={(e) => setQuery(e.target.value.toLowerCase())}
        placeholder="Enter Pokémon name"
      />
      <button onClick={fetchPokemon}>Search</button>

      {loading && <p>Loading...</p>}

      {pokemon && pokemon.name ? (
        <div style={{ marginTop: 20 }}>
          <h2>{pokemon.name.toUpperCase()}</h2>
          <img src={pokemon.sprites.front_default} alt={pokemon.name} />
          <p>Height: {pokemon.height}</p>
          <p>Weight: {pokemon.weight}</p>
          <p>Type: {pokemon.types.map(t => t.type.name).join(', ')}</p>
        </div>
      ) : !loading && <p>No Pokémon found.</p>}
    </div>
  );
}

export default App;
