import React, { useEffect, useState, useCallback } from 'react';

function App() {
  const [pokemon, setPokemon] = useState(null);
  const [loading, setLoading] = useState(false);
  const [query, setQuery] = useState("");

  const fetchPokemon = useCallback(async () => {
    if (!query) return;
    setLoading(true);
    try {
      const res = await fetch(`http://localhost:3001/api/pokemon/${query}`);
      const data = await res.json();
      setPokemon(data);
    } catch (err) {
      console.error("Fetch error:", err);
      setPokemon(null);
    }
    setLoading(false);
  }, [query]);

  useEffect(() => {
    fetchPokemon();
  }, [fetchPokemon]);

  return (
    <div style={styles.container}>
      <h1 style={styles.header}>Pokémon Search</h1>
      <div style={styles.searchContainer}>
        <input
          style={styles.input}
          value={query}
          onChange={(e) => setQuery(e.target.value.toLowerCase())}
          placeholder="Enter Pokémon name"
        />
        <button style={styles.button} onClick={fetchPokemon}>Search</button>
      </div>

      {loading && <p style={styles.loading}>Loading...</p>}

      {pokemon && pokemon.name ? (
        <div style={styles.pokemonContainer}>
          <h2 style={styles.pokemonName}>{pokemon.name.toUpperCase()}</h2>
          <img
            src={pokemon.sprites.front_default}
            alt={pokemon.name}
            style={styles.pokemonImage}
          />
          <div style={styles.detailsContainer}>
            <p><strong>Height:</strong> {pokemon.height}</p>
            <p><strong>Weight:</strong> {pokemon.weight}</p>
            <p><strong>Type:</strong> {pokemon.types.map(t => t.type.name).join(', ')}</p>
          </div>
        </div>
      ) : !loading && <p>No Pokémon found.</p>}
    </div>
  );
}

const styles = {
  container: {
    padding: 20,
    fontFamily: 'Arial, sans-serif',
    maxWidth: 600,
    margin: '0 auto',
    textAlign: 'center',
    backgroundColor: '#f7f7f7',
    borderRadius: 10,
    boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)',
  },
  header: {
    fontSize: '2em',
    marginBottom: 20,
    color: '#333',
  },
  searchContainer: {
    marginBottom: 20,
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  input: {
    padding: 10,
    width: '200px',
    fontSize: '1em',
    marginRight: 10,
    borderRadius: 5,
    border: '1px solid #ccc',
    outline: 'none',
    transition: 'border 0.3s ease',
  },
  inputFocus: {
    border: '1px solid #007bff',
  },
  button: {
    padding: '10px 20px',
    fontSize: '1em',
    backgroundColor: '#007bff',
    color: 'white',
    border: 'none',
    borderRadius: 5,
    cursor: 'pointer',
    transition: 'background-color 0.3s ease',
  },
  buttonHover: {
    backgroundColor: '#0056b3',
  },
  loading: {
    fontSize: '1.2em',
    color: '#007bff',
  },
  pokemonContainer: {
    marginTop: 20,
    textAlign: 'center',
    padding: 20,
    backgroundColor: '#fff',
    borderRadius: 10,
    boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)',
  },
  pokemonName: {
    fontSize: '2em',
    fontWeight: 'bold',
    color: '#333',
  },
  pokemonImage: {
    width: '150px',
    height: '150px',
    marginBottom: 20,
  },
  detailsContainer: {
    textAlign: 'left',
    fontSize: '1.1em',
    color: '#555',
  },
};

export default App;
