// server.js

console.log("Server starting...");

const express = require('express');
const axios = require('axios');
const cors = require('cors');
const app = express();
const port = 5000;

app.use(cors());

app.get('/api/pokemon/:name', async (req, res) => {
  const pokemonName = req.params.name;
  console.log(`Received request for Pokémon: ${pokemonName}`);

  try {
    const response = await axios.get(
      `https://pokeapi.co/api/v2/pokemon/${pokemonName}`,
      {
        headers: {
          'User-Agent': 'MyPokemonApp/1.0',
          'Accept': 'application/json',
        }
      }
    );
    console.log(`Data for ${pokemonName} fetched successfully`);
    res.json(response.data);
  } catch (error) {
    console.error(`Error fetching data for ${pokemonName}:`, error.message);
    res.status(500).json({ message: 'Something went wrong or Pokémon not found' });
  }
});

app.listen(port, () => {
  console.log(`Server running on http://localhost:${port}`);
});
